// more readable way to do it
const Form = (props) => {
  return (
    <div>
      <form onSubmit={props.getWeather}>
        <input type="text" name="city" placeholder="city"/>
        <input type="text" name="country" placeholder="country"/>
        <button>Get Weather</button>
      </form>
    </div>
  )
}

export default Form;
