import React, { Component } from 'react';
import Titles from './components/Titles'
import Form from './components/Form'
import Weather from './components/Weather'

// Reference from: https://www.youtube.com/watch?v=204C9yNeOYI

// Open Weather API
const API_KEY = "067de4198b8d098ada70ce4150be82c8"

class App extends Component {
  getWeather = async (e) => {
    e.preventDefault
    const api_call = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=&appid=${API_KEY}&units=metric`);
    const data = await api_call.json();
    console.log(data);
  }
  render() {
    return(
      <div>
        <Titles />
        <Form getWeather={this.getWeather} />
        <Weather />
      </div>
    );
  }
}

export default App;
